mapper.update:
UPDATE game_account
SET login_id=#{loginId},
    nickname=#{nickname},
    rank_tier_group=#{rankTier.group},
    rank_tier_level=#{rankTier.level}
WHERE id = #{id}

mapper.select:
SELECT id, login_id, nickname, rank_tier_group, rank_tier_level
FROM game_account
WHERE id = #{id}

SELECT id, login_id, nickname, rank_tier_group, rank_tier_level
FROM game_account
WHERE login_id = #{loginId}
